mapper.select:
SELECT * 
  FROM admin
 WHERE admin_id = #{admin_id}
   AND admin_pw = #{admin_pw}

mapper.update:
UPDATE good
SET good_name = #{good_name}, good_price=#{good_price}, good_tag=#{good_tag}, good_stock=#{good_stock}, good_detail=#{good_detail},spon_no=#{spon_no}
WHERE good_no = #{good_no}

UPDATE spon
SET spon_name = #{spon_name}, spon_iscampaign= #{spon_iscampaign}, spon_end=#{spon_end}, spon_url=#{spon_url}, spon_comment=#{spon_comment}
WHERE spon_no = #{spon_no}
