mapper.select:
SELECT * 
  FROM user
 WHERE user_id = #{user_id}
   AND user_pw = #{user_pw}


SELECT COUNT(*) FROM user
WHERE user_name=#{user_name}
AND user_tel = #{user_tel}


SELECT COUNT(*) FROM user
WHERE user_id=#{user_id}
AND user_name = #{user_pw}
AND user_tel = #{user_tel}
AND user_jumin = #{user_jumin}
