mapper.insert:
INSERT INTO flea(flea_title, flea_price, flea_writer, flea_content, flea_date,

flea_img

)
VALUES (#{flea_title}, #{flea_price}, #{flea_writer}, #{flea_content}, sysdate(),

#{flea_img}

)

mapper.update:
UPDATE flea_com
SET flea_com_content = #{flea_com_content}
WHERE flea_com_no = #{flea_com_no}

mapper.select:
SELECT * FROM flea
ORDER BY flea_no desc
LIMIT #{pageStart}, #{perPageNum}

SELECT * FROM flea
WHERE flea_isokay = '1'
LIMIT #{pageStart}, #{perPageNum}

SELECT * FROM flea
${searchType} LIKE '%${keyword}%'
ORDER BY flea_no LIMIT #{pageStart}, #{perPageNum}

mapper.update:
UPDATE flea
SET flea_title = #{flea_title}, flea_content = #{flea_content}, flea_price = #{flea_price}, flea_date = sysdate(),

flea_img = #{flea_img}

WHERE flea_no = #{flea_no}
